module VideoHelper\n     def embed_video(video_url)\n       # Check the video source and generate the appropriate embed HTML\n       case video_url\n       when /youtube\.com/\n         youtube_embed(video_url)\n       when /vimeo\.com/\n         vimeo_embed(video_url)\n       # Add more cases for other video sources as needed\n       else\n         content_tag(:p, "Unsupported video source")\n       end\n     end\n\n     private\n\n     def youtube_embed(url)\n       video_id = extract_youtube_id(url)\n       content_tag(:iframe, '', width: 560, height: 315, src: "https://www.youtube.com/embed/#{video_id}", frameborder: 0, allowfullscreen: true)\n     end\n\n     def vimeo_embed(url)\n       video_id = extract_vimeo_id(url)\n       content_tag(:iframe, '', width: 560, height: 315, src: "https://player.vimeo.com/video/#{video_id}", frameborder: 0, allow: 'autoplay; fullscreen', allowfullscreen: true)\n     end\n\n     def extract_youtube_id(url)\n       # Extract the YouTube video ID from the URL\n       url[/v=([^&]+)/, 1]\n     end\n\n     def extract_vimeo_id(url)\n       # Extract the Vimeo video ID from the URL\n       url[/vimeo\.com\/(\d+)/, 1]\n     end\n   end