class ExternalVideo < ApplicationRecord\n     # Validations and other model logic\n     validates :video_url, presence: true, format: { with: URI::regexp(%w[http https]) }\n\n     # Add custom validations to restrict URLs to specific domains\n     validate :allowed_video_source\n\n     private\n\n     def allowed_video_source\n       allowed_sources = %w[youtube.com vimeo.com]\n       unless allowed_sources.any? { |domain| video_url.include?(domain) }\n         errors.add(:video_url, "is not allowed")\n       end\n     end\n   end