# config/deploy.rb\n\nset :application, "my_php_app"\nset :repo_url, "git@example.com:me/my_repo.git"\n\n# Define roles for Linux and Windows servers\nrole :linux_app, %w{user@linux-server1 user@linux-server2}\nrole :windows_app, %w{user@windows-server1 user@windows-server2}\n\n# Define tasks for Linux deployment\nnamespace :deploy do\n  desc 'Deploy to Linux servers'\n  task :to_linux do\n    on roles(:linux_app) do\n      execute "cd /path/to/app && git pull && composer install"\n    end\n  end\n\n  desc 'Deploy to Windows servers'\n  task :to_windows do\n    on roles(:windows_app) do\n      execute "powershell -Command \"cd C:\\path\\to\\app; git pull; composer install\""\n    end\n  end\n\n  desc 'Deploy to all servers'\n  task :all do\n    invoke 'deploy:to_linux'\n    invoke 'deploy:to_windows'\n  end\nend