module Superpower\n    # instance method\n    def turn_invisible\n        # implementation\n    end\n\n    # module method\n    def self.turn_into_toad(bad_guy)\n        # implementation\n    end\n\n    module Fly\n        def flap_wings\n            # implementation\n        end\n    end\nend\n\nclass Superman\n    include Superpower\n    include Superpower::Fly  # Include the nested module explicitly\n\n    def run_away\n        # Call flap_wings\n        flap_wings\n\n        # Call turn_invisible\n        turn_invisible\n    end\n\n    def see_bad_guys(bad_guy = 'lex_luthor')\n        # This is correct for calling the module method\n        Superpower.turn_into_toad(bad_guy)\n    end\nend