# In your Ruby application\nrequire 'sinatra'\nrequire 'active_record'\n\nget '/health' do\n  begin\n    # Check database connection\n    ActiveRecord::Base.connection.execute("SELECT 1")\n    \n    # Check latest email log entry\n    latest_email = EmailLog.order(created_at: :desc).first\n    if latest_email\n      status 200\n      body "OK: Latest email logged at #{latest_email.created_at}"\n    else\n      status 500\n      body "Error: No email logs found"\n    end\n  rescue => e\n    status 500\n    body "Error: #{e.message}"\n  end\nend