@foreach (var heading in new[] { "ID", "TimeLogged", "Technician", "Customer", "Category", "Priority", "Status" })\n{\n    <th>\n        @Html.ActionLink(heading, "Open", new { sort = heading, filterType = ViewBag.CurrentFilterType, filterValue = ViewBag.CurrentFilterValue })\n    </th>\n}\n\n<!-- Example filter dropdowns -->\n<select id="filterType" name="filterType">\n    <option value="Technician">Technician</option>\n    <option value="Customer">Customer</option>\n    <option value="Category">Category</option>\n    <option value="Priority">Priority</option>\n    <option value="Status">Status</option>\n</select>\n<input type="text" id="filterValue" name="filterValue" />\n<button type="submit">Filter</button>