<!-- app/views/meals/_form.html.erb -->\n   <%= form_with(model: meal, local: true) do |f| %>\n     <%= f.label :name %>\n     <%= f.text_field :name %>\n\n     <div id="foods">\n       <%= f.fields_for :foods do |ff| %>\n         <%= render 'food_fields', f: ff %>\n       <% end %>\n     </div>\n\n     <div class="links">\n       <%= link_to_add_fields "Add Food", f, :foods %>\n     </div>\n\n     <%= f.submit %>\n   <% end %>