<!-- app/views/your_model/_form.html.erb -->\n<%= form_with(model: @your_model, local: true) do |form| %>\n  <div class="form-group">\n    <%= form.label :name %>\n    <%= form.hidden_field :your_model_id, id: 'your_model_id' %>\n    <%= text_field_tag :your_model_name, '', id: 'your_model_name', class: 'form-control' %>\n  </div>\n\n  <div class="form-group">\n    <%= form.submit %>\n  </div>\n<% end %>