require 'csv'\n\n# Read the CSV file and store it in a variable\ncsv_file_path = 'path/to/your/huge_file.csv'\n\n# Parse the CSV file and use headers\ncsv_data = CSV.read(csv_file_path, headers: true)\n\n# Access a particular column by its header name\ncolumn_data = csv_data['header_name']\n\n# Iterate through rows and access data by header name\ncsv_data.each do |row|\n  puts row['header_name']\nend