<% @scrape.each do |room| %>\n  <tr id="page_<%= room.id %>">\n    <th scope="row" class="<%= cycle("spec", "specalt") %>"><%= h room.name %></th>\n    <% room.attributes.each do |key, value| %>\n      <% if key.start_with?('day') %>\n        <td class="<%= cycle("spec", "specalt") %>"><%= h value %></td>\n      <% end %>\n    <% end %>\n    <td class="<%= cycle("spec", "specalt") %>"><%= select_tag("room[#{room.id}]", options_for_select(0..room.spots, 0)) %></td>\n  </tr>\n<% end %>