<tr id="business_<%= business.id %>">\n  <td><%=h business.name %></td>\n  <td><%=h business.address %></td>\n  <td><%=h business.business_category.name %></td>\n  <td><%=h business.description %></td>\n  <td><%= link_to(image_tag('/images/accept.png', alt: 'Approve'), approve_business_path(business), method: :post, remote: true) %></td>\n  <td><%= link_to(image_tag('/images/bin.png', alt: 'Remove'), business, method: :delete, data: { confirm: 'Are you sure?' }, remote: true) %></td>\n</tr>