<ul id="section-list" style="cursor: move">\n  <% @form.sections.each do |s| %>\n    <li id="section_<%= s.id %>">\n      <%= s.name %>\n      <ul id="question-list-<%= s.id %>" class="connectedSortable" style="cursor: move">\n        <% s.questions.each do |q| %>\n          <li id="question_<%= q.id %>"><%= q.content %></li>\n        <% end %>\n      </ul>\n    </li>\n  <% end %>\n</ul>