<!-- app/views/contact_forms/new.html.erb -->\n   <h1>Contact Us</h1>\n\n   <%= form_with model: @contact_form, url: contact_forms_path do |form| %>\n     <% if @contact_form.errors.any? %>\n       <div id="error_explanation">\n         <h2><%= pluralize(@contact_form.errors.count, "error") %> prohibited this form from being saved:</h2>\n         <ul>\n           <% @contact_form.errors.full_messages.each do |message| %>\n             <li><%= message %></li>\n           <% end %>\n         </ul>\n       </div>\n     <% end %>\n\n     <div>\n       <%= form.label :name %>\n       <%= form.text_field :name %>\n     </div>\n\n     <div>\n       <%= form.label :email %>\n       <%= form.email_field :email %>\n     </div>\n\n     <div>\n       <%= form.label :message %>\n       <%= form.text_area :message %>\n     </div>\n\n     <div>\n       <%= form.submit "Send" %>\n     </div>\n   <% end %>