require 'nokogiri'\nrequire 'open-uri'\n\ndef fetch_meta_tags(url)\n  doc = Nokogiri::HTML(URI.open(url))\n  meta_tags = {}\n\n  doc.css('meta').each do |meta|\n    name = meta.attribute('name') || meta.attribute('property')\n    content = meta.attribute('content')\n    meta_tags[name.value] = content.value if name && content\n  end\n\n  meta_tags\nend\n\nurl = 'https://example.com'\nmeta_tags = fetch_meta_tags(url)\nputs meta_tags