# app/controllers/reports_controller.rb\n   class ReportsController < ApplicationController\n     def generate_pdf\n       filename = "report.pdf"\n       content = "This is a sample PDF generated using iText library in Java."\n\n       # Initialize the Java class and call the method\n       java_import 'PdfGenerator'\n       pdf_generator = PdfGenerator.new\n       pdf_generator.createPdf(filename, content)\n\n       send_file filename, type: 'application/pdf', disposition: 'inline'\n     end\n   end