<!-- app/views/contact/new.html.erb -->\n<%= form_with model: @contact_form, url: contact_path, local: true do |form| %>\n  <div>\n    <%= form.label :subject %>\n    <%= form.text_field :subject %>\n    <%= @contact_form.errors.full_messages_for(:subject).join(", ") %>\n  </div>\n\n  <div>\n    <%= form.label :message %>\n    <%= form.text_area :message %>\n    <%= @contact_form.errors.full_messages_for(:message).join(", ") %>\n  </div>\n\n  <div>\n    <%= form.submit "Send" %>\n  </div>\n<% end %>