# In your test setup\ncontext "on GET /messages" do\n  setup do\n    @me = FactoryBot.create(:sender)\n    @my_message = FactoryBot.create(:my_message, sender: @me)\n    @somebody_elses_message = FactoryBot.create(:message)\n    sign_in_as(@me)\n    get :index\n  end\n\n  should "only assign my messages" do\n    assert_contains(assigns(:messages), @my_message)\n    assert_does_not_contain(assigns(:messages), @somebody_elses_message)\n  end\nend