class YourController < ApplicationController\n  def create\n    if request.xhr?\n      # Handle AJAX request\n      respond_to do |format|\n        format.json { render json: { message: "This is an AJAX request" } }\n      end\n    else\n      # Handle normal POST request\n      respond_to do |format|\n        format.html { redirect_to some_path, notice: "This is a normal POST request" }\n      end\n    end\n  end\nend