require 'win32ole'\n\n# Connection strings\nsource_db_path = "C:\\path\\to\\SourceDatabase1.accdb"\ndestination_db_path = "C:\\path\\to\\NewDatabase.accdb"\n\nsource_conn = WIN32OLE.new('ADODB.Connection')\ndestination_conn = WIN32OLE.new('ADODB.Connection')\n\nsource_conn.Open("Provider=Microsoft.ACE.OLEDB.12.0;Data Source=#{source_db_path};")\ndestination_conn.Open("Provider=Microsoft.ACE.OLEDB.12.0;Data Source=#{destination_db_path};")\n\ntables = ["Table1", "Table2", "Table3", "Table4", "Table5", "Table6", "Table7", "Table8", "Table9", "Table10", "Table11", "Table12", "Table13", "Table14", "Table15"]\n\ntables.each do |table|\n  sql = "INSERT INTO #{table} SELECT * FROM [;DATABASE=#{source_db_path}].#{table}"\n  destination_conn.Execute(sql)\nend\n\nsource_conn.Close\ndestination_conn.Close