<tr id="ride_<%=h ride.id %>">\n  <td><div><%=h ride.name %></div></td>\n  <td><div><%=h ride.land %></div></td>\n  <td align="right"><div><%=h ride.height_restriction %></div></td>\n  <td>\n    <div>\n      <%= link_to_remote 'remove', \n        :url => { :action => :destroy, :ride_id => ride.id }, \n        :html => { :onclick => "Effect.SlideUp('ride_#{ride.id}'); return false;" } %>\n    </div>\n  </td>\n</tr>