<% href = url_for post_path(:id => post) %>\n\n<h1 class="title"><%= post.title %></h1>\n<h2 class="published_on"><%= post.author %> wrote this <%= time_ago_in_words(post.published_on) %> ago</h2>\n<div class="body">\n  <% if defined?(length) %>\n    <%= truncate_html(post.body, :length => length, :omission => "&hellip;<h1><a class='more' href='#{href}' data-post-id='#{post.id}'>Click here for more!</a></h1>") %>\n  <% else %>\n    <%= post.body %>\n  <% end %>\n</div>