<!-- app/views/analytics/index.html.erb -->\n   <h1>Analytics</h1>\n\n   <form method="get">\n     <label for="start_date">Start Date:</label>\n     <input type="date" id="start_date" name="start_date" value="<%= @analytics_data.start_date %>">\n\n     <label for="end_date">End Date:</label>\n     <input type="date" id="end_date" name="end_date" value="<%= @analytics_data.end_date %>">\n\n     <button type="submit">Filter</button>\n   </form>\n\n   <h2>Metrics</h2>\n   <ul>\n     <% @analytics_data.metrics.each do |key, value| %>\n       <li><%= key %>: <%= value %></li>\n     <% end %>\n   </ul>