require 'prawn'\n\nPrawn::Document.generate("example.pdf") do\n  # Define the image dimensions and position\n  image_path = "path_to_your_image.jpg"\n  image_width = 100\n  image_height = 100\n  image_x_position = bounds.width - image_width\n  image_y_position = cursor\n\n  # Place the image\n  image image_path, at: [image_x_position, image_y_position], width: image_width, height: image_height\n\n  # Move the cursor down to leave space for the image\n  move_down image_height\n\n  # Add text that flows around the image\n  text "Lorem ipsum dolor sit amet, consectetur adipiscing elit. " \\n       "Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. " \\n       "Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. " \\n       "Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. " \\n       "Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum. " * 5,\n       indent_paragraphs: 20, leading: 2\nend