start_date = '2023-10-01' # Example start date for October 2023\nend_date = '2023-10-31'   # Example end date for October 2023\n\nclients = Client.find_by_sql([\n  "SELECT DISTINCT clients.*\n   FROM clients\n   LEFT JOIN invoices ON clients.id = invoices.client_id AND invoices.date BETWEEN ? AND ?\n   LEFT JOIN deliveries ON clients.id = deliveries.client_id AND deliveries.date BETWEEN ? AND ?\n   WHERE invoices.id IS NOT NULL OR deliveries.id IS NOT NULL",\n  start_date, end_date, start_date, end_date\n])