event_participants.sort! { |a, b| Participant.field_event_sort(a, b) }\n\nclass Participant\n  def self.field_event_sort(a, b)\n    a_value = parse_time_distance(a.time_distance)\n    b_value = parse_time_distance(b.time_distance)\n    b_value <=> a_value\n  end\n\n  def self.parse_time_distance(time_distance)\n    match = time_distance.match(/(\d+):(\d+).(\d+)/)\n    return 0 unless match\n\n    minutes_or_feet = match[1].to_i\n    seconds_or_inches = match[2].to_i\n    fraction = match[3].to_i\n\n    (minutes_or_feet * 60 * 1000) + (seconds_or_inches * 1000) + fraction\n  end\nend