# classes_controller.rb\n   class ClassesController < ApplicationController\n     def update\n       @class = Class.find(params[:id])\n       if @class.update(class_params)\n         redirect_to @class, notice: 'Class was successfully updated.'\n       else\n         render :edit\n       end\n     end\n\n     private\n\n     def class_params\n       params.require(:class).permit(:name, :description, student_ids: [])\n     end\n   end