class User < ActiveRecord::Base\n  has_many :posts, class_name: 'Post', foreign_key: 'author_id'\n  has_many :comments, class_name: 'Comment', foreign_key: 'author_id'\n\n  def recent_posts_on_class\n    Post.where(author_id: self.id).order(created_at: :asc).limit(5)\n  end\n\n  def recent_posts_on_self\n    self.posts.order(created_at: :asc).limit(5)\n  end\n\n  def all_recent_posts_on_class\n    Post.order(created_at: :asc).limit(5)\n  end\nend