require 'net/smtp'\nrequire 'openssl'\n\nsmtp = Net::SMTP.new('smtp.gmail.com', 587)\nsmtp.enable_starttls\n\nbegin\n  smtp.start('gmail.com', 'your_email@gmail.com', 'your_password', :login) do |smtp|\n    message = <<~MESSAGE_END\n      From: Your Name <your_email@gmail.com>\n      To: Destination Address <destination@example.com>\n      Subject: Test Mail\n\n      This is a test email.\n    MESSAGE_END\n\n    smtp.send_message message, 'your_email@gmail.com', 'destination@example.com'\n  end\nrescue Net::SMTPAuthenticationError => e\n  puts "Authentication error: #{e.message}"\nrescue Net::SMTPFatalError => e\n  puts "Fatal error: #{e.message}"\nrescue Net::SMTPSyntaxError => e\n  puts "Syntax error: #{e.message}"\nrescue Net::SMTPServerBusy => e\n  puts "Server busy: #{e.message}"\nrescue Net::SMTPUnknownError => e\n  puts "Unknown error: #{e.message}"\nrescue OpenSSL::SSL::SSLError => e\n  puts "SSL error: #{e.message}"\nrescue => e\n  puts "General error: #{e.message}"\nend