class ArticlesController < ApplicationController\n  def index\n    @articles = Article.all\n  end\n\n  def show\n    @article = Article.find(params[:id])\n  end\n\n  def new\n    @article = Article.new\n  end\n\n  def create\n    @article = Article.new(article_params)\n    if @article.save\n      redirect_to @article\n    else\n      render 'new'\n    end\n  end\n\n  private\n\n  def article_params\n    params.require(:article).permit(:title, :text)\n  end\nend