# A simple DSL for defining a workflow in Ruby\nclass Workflow\n  def initialize\n    @steps = []\n  end\n\n  def step(name, &block)\n    @steps << { name: name, action: block }\n  end\n\n  def run\n    @steps.each do |step|\n      puts "Running step: #{step[:name]}"\n      step[:action].call\n    end\n  end\nend\n\n# Using the DSL to define a workflow\nworkflow = Workflow.new\nworkflow.step("Initialize") { puts "Initializing..." }\nworkflow.step("Process Data") { puts "Processing data..." }\nworkflow.step("Finalize") { puts "Finalizing..." }\n\n# Execute the workflow\nworkflow.ru