require 'gtk3'\n\n   class MultiSelectListBox\n     def initialize\n       # Initialize GTK\n       Gtk.init\n\n       # Load the Glade file\n       builder = Gtk::Builder.new\n       builder.add_from_file("interface.glade")\n\n       # Get the window and TreeView from the builder\n       @window = builder.get_object("main_window")\n       @treeview = builder.get_object("treeview")\n\n       # Setup the ListStore model with one column (String)\n       @liststore = Gtk::ListStore.new(String)\n       @treeview.model = @liststore\n\n       # Add a renderer for the text column\n       renderer = Gtk::CellRendererText.new\n       column = Gtk::TreeViewColumn.new("Items", renderer, text: 0)\n       @treeview.append_column(column)\n\n       # Allow multiple selection\n       selection = @treeview.selection\n       selection.mode = Gtk::SelectionMode::MULTIPLE\n\n       # Populate the ListStore with some data\n       add_items(["Item 1", "Item 2", "Item 3", "Item 4"])\n\n       # Show the window\n       @window.signal_connect('destroy') { Gtk.main_quit }\n       @window.show_all\n     end\n\n     def add_items(items)\n       items.each do |item|\n         iter = @liststore.append\n         iter[0] = item\n       end\n     end\n\n     def run\n       Gtk.main\n     end\n   end\n\n   app = MultiSelectListBox.new\n   app.ru