require 'nokogiri'\n\n   # Load your XHTML or ATOM content\n   xhtml_content = File.read('path/to/your/file.xhtml')\n   atom_content = File.read('path/to/your/file.atom')\n\n   # Parse the content with Nokogiri\n   xhtml_doc = Nokogiri::XML(xhtml_content)\n   atom_doc = Nokogiri::XML(atom_content)\n\n   # Validate against DTD\n   xhtml_errors = xhtml_doc.external_subset.validate(xhtml_doc)\n   atom_errors = atom_doc.external_subset.validate(atom_doc)\n\n   if xhtml_errors.empty?\n     puts "XHTML is valid."\n   else\n     puts "XHTML validation errors:"\n     xhtml_errors.each { |error| puts error.message }\n   end\n\n   if atom_errors.empty?\n     puts "ATOM is valid."\n   else\n     puts "ATOM validation errors:"\n     atom_errors.each { |error| puts error.message }\n   end