class Post < ApplicationRecord\n  self.primary_key = 'post_id'\n  has_many :comments, foreign_key: 'post_id', class_name: 'Comment'\n  \n  has_one :question, foreign_key: 'post_id', class_name: 'Question'\n  has_one :answer, foreign_key: 'post_id', class_name: 'Answer'\nend\n\nclass Comment < ApplicationRecord\n  belongs_to :post, foreign_key: 'post_id'\nend\n\nclass Question < ApplicationRecord\n  self.primary_key = 'post_id'\n  belongs_to :post, foreign_key: 'post_id'\n  has_many :answers, foreign_key: 'question_id'\n  \n  validate :post_type_must_be_q\n\n  private\n\n  def post_type_must_be_q\n    errors.add(:post_type, "must be 'Q'") unless post_type == 'Q'\n  end\nend\n\nclass Answer < ApplicationRecord\n  self.primary_key = 'post_id'\n  belongs_to :post, foreign_key: 'post_id'\n  belongs_to :question, foreign_key: 'question_id'\n  \n  validate :post_type_must_be_a\n\n  private\n\n  def post_type_must_be_a\n    errors.add(:post_type, "must be 'A'") unless post_type == 'A'\n  end\nend