class SessionsController < ApplicationController\n  def destroy\n    # Your existing logout logic\n\n    # Manually delete specific Facebooker cookies\n    cookies.delete(:fbsetting_0b78c8f2c95ce671470bdcb1c19e5070)\n    \n    # You might need to delete other related cookies as well\n    cookies.each do |key, _|\n      if key.start_with?("fbsr_", "fbm_")\n        cookies.delete(key)\n      end\n    end\n\n    # Redirect or render as needed\n    redirect_to root_path, notice: "Successfully logged out."\n  end\nend