# spec/lib/tasks/my_task_spec.rb\nrequire 'rails_helper'\nrequire 'rake'\n\nRSpec.describe 'my_namespace:my_task' do\n  before do\n    Rake.application.rake_require('tasks/my_namespace/my_task')\n    Rake::Task.define_task(:environment)\n  end\n\n  let(:task) { Rake::Task['my_namespace:my_task'] }\n\n  it 'executes the task successfully' do\n    expect { task.invoke }.not_to raise_error\n  end\nend