# app/controllers/ratings_controller.rb\n    class RatingsController < ApplicationController\n      def create\n        @rateable = find_rateable\n        @rating = @rateable.ratings.create(rating_params)\n        respond_to do |format|\n          format.js\n        end\n      end\n\n      private\n\n      def find_rateable\n        params[:rateable_type].constantize.find(params[:rateable_id])\n      end\n\n      def rating_params\n        params.require(:rating).permit(:score)\n      end\n    end