require 'sequel'\n\n# Create an in-memory SQLite database\nDB = Sequel.sqlite\n\n# Define some tables\nDB.create_table :table1 do\n  Integer :id\n  String :value\nend\n\nDB.create_table :table2 do\n  Integer :id\n  String :description\nend\n\n# Insert data into tables\ntable1 = DB[:table1]\ntable2 = DB[:table2]\ntable1.insert(:id => 1, :value => 'A')\ntable1.insert(:id => 2, :value => 'B')\ntable1.insert(:id => 3, :value => 'C')\n\ntable2.insert(:id => 1, :description => 'Desc1')\ntable2.insert(:id => 2, :description => 'Desc2')\ntable2.insert(:id => 4, :description => 'Desc4')\n\n# Perform a SQL-like join operation\nresult = table1.join(:table2, :id => :id).all\nputs result