# In your controller\ndef show\n  @schedule = Schedule.find(params[:id])\n  if @schedule.assignment.nil?\n    # Handle the case where the assignment is missing\n    flash[:error] = "Assignment is missing for this schedule."\n    redirect_to schedules_path\n  end\nend\n\n# In your view\n<% if @schedule.assignment %>\n  <%= @schedule.assignment.details %>\n<% else %>\n  <p>Assignment data is not available.</p>\n<% end %>