require 'erubis'\n\ntemplate = <<-EOF\n<!DOCTYPE html>\n<html>\n<head>\n  <title><%= @title %></title>\n</head>\n<body>\n  <h1>Welcome to <%= @title %>!</h1>\n  <p>This page was generated using Eruby.</p>\n</body>\n</html>\nEOF\n\n@title = "My Ruby Site"\neruby = Erubis::Eruby.new(template)\nresult = eruby.result(binding)\n\nFile.write('output.html', result)\nputs "HTML file generated!"