require 'sequel'\n    require 'yaml'\n\n    db_config = YAML.load_file('database.yml')['development']\n    DB = Sequel.connect(db_config)\n\n    # Example of defining a migration\n    Sequel.migration do\n      change do\n        create_table(:users) do\n          primary_key :id\n          String :name\n          String :email\n          DateTime :created_at\n          DateTime :updated_at\n        end\n      end\n    end