require 'test/unit'\nrequire 'nokogiri'\n\nclass HTMLComparisonTest < Test::Unit::TestCase\n\n  def normalize_html(html)\n    Nokogiri::HTML(html).to_s.gsub(/\s+/, ' ').strip\n  end\n\n  def test_html_comparison\n    generated_html = "<div>    Some  text    </div>"\n    expected_html = "<div>Some text</div>"\n\n    normalized_generated_html = normalize_html(generated_html)\n    normalized_expected_html = normalize_html(expected_html)\n\n    assert_equal(normalized_expected_html, normalized_generated_html)\n  end\nend