module LoginHelper\n  def login_with_credentials(type)\n    # Implement your login logic here\n    if type == "valid"\n      # Perform login with valid credentials\n    elsif type == "invalid"\n      # Perform login with invalid credentials\n    else\n      raise "Unknown credential type: #{type}"\n    end\n  end\nend\n\nWorld(LoginHelper)\n\nGiven(/^I login with (.*) credentials$/) do |type|\n  login_with_credentials(type)\nend\n\nGiven(/^I login successfully$/) do\n  login_with_credentials("valid")\nend