# Example action in App-B for initiating the OAuth2 flow\n   def login\n     redirect_to client.auth_code.authorize_url(redirect_uri: 'https://app-b.com/callback')\n   end\n\n   # Example callback action in App-B\n   def callback\n     token = client.auth_code.get_token(params[:code], redirect_uri: 'https://app-b.com/callback')\n     user_info = token.get('/api/userinfo').parsed\n     # Log in or register the user in App-B using user_info\n   end