class ListItemsController < ApplicationController\n  def approve\n    @list_item = ListItem.find(params[:id])\n    if @list_item.update(approved: params[:approved], approval_date: Time.current, approved_by: current_user)\n      render json: { success: true, approved: @list_item.approved }\n    else\n      render json: { success: false, errors: @list_item.errors.full_messages }\n    end\n  end\nend