require 'mysql2'\n\n# Configure your database connection details\nclient = Mysql2::Client.new(\n  host: 'localhost',\n  username: 'your_username',\n  password: 'your_password',\n  database: 'your_database'\n)\n\n# Execute the SQL query and fetch the result\nresult = client.query("SHOW CREATE TABLE your_table_name")\n\n# Extract the CREATE TABLE statement as a string\ncreate_table_statement = result.first['Create Table']\n\n# Output or process the CREATE TABLE statement string\nputs create_table_statement\n\n# Example: Check for a specific key and delete it if it exists\nif create_table_statement.include?('KEY your_key_name')\n  # Your code to delete the key\n  client.query("ALTER TABLE your_table_name DROP KEY your_key_name")\n  puts "Key 'your_key_name' has been deleted."\nend\n\n# Close the database connection\nclient.close