class UserMailer < ApplicationMailer\n  def encrypted_email(user)\n    @user = user\n    mail(to: @user.email, subject: 'Encrypted Email') do |format|\n      format.text { render plain: encrypt_message("Your message here") }\n    end\n  end\n\n  private\n\n  def encrypt_message(message)\n    crypto = GPGME::Crypto.new\n    recipient_key = GPGME::Key.find(:public, @user.email).first\n    encrypted = crypto.encrypt(message, recipients: recipient_key)\n    encrypted.to_s\n  end\nend