module CustomSetter\n  def self.included(base)\n    base.extend(ClassMethods)\n  end\n\n  module ClassMethods\n    def custom_setter(*fields)\n      fields.each do |field|\n        define_method("#{field}=") do |value|\n          begin\n            Date.parse(value)\n          rescue ArgumentError\n            @dates_bad = true\n          end\n          write_attribute(field, value)\n        end\n      end\n    end\n  end\nend\n\n# Usage in your ActiveRecord subclass\nclass YourModel < ActiveRecord::Base\n  include CustomSetter\n\n  custom_setter :check_sent, :another_field, :yet_another_field\nend