require 'prometheus/client'\n   require 'prometheus/client/push'\n\n   prometheus = Prometheus::Client.registry\n\n   # Define a gauge metric\n   gauge = Prometheus::Client::Gauge.new(:ruby_worker_health, docstring: 'Health of Ruby worker', labels: [:worker_id])\n   prometheus.register(gauge)\n\n   # Update the gauge with health status\n   gauge.set(1, labels: { worker_id: 'worker_1' }) # 1 for healthy, 0 for unhealthy\n\n   # Push the metrics to a Prometheus Pushgateway (optional)\n   pushgateway = Prometheus::Client::Push.new('my_job', instance: 'worker_1', gateway: 'http://localhost:9091')\n   pushgateway.add(prometheus)