#!/usr/local/bin/ruby\n\nrequire 'cgi'\n\n# Initialize the CGI object\ncgi = CGI.new\n\n# Create a new cookie\ncookie = CGI::Cookie.new(\n  "name"    => "rubyweb",\n  "value"   => ["CustID=123", "Part=ABC"],\n  "expires" => Time.now + 3600 # expires in 1 hour\n)\n\n# Output headers along with the cookie and the HTML content\ncgi.out("cookie" => [cookie]) {\n  # Generate the HTML content\n  <<-HTML\n  <!DOCTYPE html>\n  <html>\n  <head>\n    <title>Cookie Test</title>\n  </head>\n  <body>\n    <h1>Cookie has been set!</h1>\n    <p>Check your browser's cookies to see the 'rubyweb' cookie.</p>\n  </body>\n  </html>\n  HTML\n}\n\n# Additional game logic can go here