require 'csv'\n   require 'active_record'\n\n   ActiveRecord::Base.establish_connection(\n     adapter: 'postgresql',\n     host: 'localhost',\n     database: 'your_database',\n     username: 'your_username',\n     password: 'your_password'\n   )\n\n   class Category < ActiveRecord::Base\n     has_many :subcategories, class_name: 'Category', foreign_key: 'parent_id'\n     belongs_to :parent, class_name: 'Category', optional: true\n   end\n\n   class ProductBlock < ActiveRecord::Base\n     belongs_to :category\n   end\n\n   class Product < ActiveRecord::Base\n     belongs_to :product_block\n   end\n\n   # Load categories\n   CSV.foreach('categories.csv', headers: true) do |row|\n     Category.create(name: row['name'], parent_id: row['parent_id'])\n   end\n\n   # Load product blocks\n   CSV.foreach('product_blocks.csv', headers: true) do |row|\n     ProductBlock.create(\n       category_id: row['category_id'],\n       long_description: row['long_description'],\n       search_terms: row['search_terms'],\n       image_names: row['image_names']\n     )\n   end\n\n   # Load products\n   CSV.foreach('products.csv', headers: true) do |row|\n     Product.create(\n       product_block_id: row['product_block_id'],\n       name: row['name'],\n       sku: row['sku'],\n       price: row['price'],\n       stock: row['stock']\n     )\n   end